$(document).ready(function () { $("#select_country").change(function () { var e = document.getElementById("label_select_state"), t = document.getElementById("select_state"); switch (this.value) { case "United States": e.innerHTML = "State*", t.outerHTML = document.getElementById("template_state_us").innerHTML; break; case "Canada": e.innerHTML = "Province*", t.outerHTML = document.getElementById("template_state_ca").innerHTML; break; default: e.innerHTML = "State/Region*", t.outerHTML = document.getElementById("template_stateregion").innerHTML } }) });